mapper.select:
select a.product_id productId, a.pv pv, a.uv uv, b.orderAmount orderAmount, b.orderUser orderUser, b.orderCnt orderCnt, b.productCnt productCnt from ( select count(distinct_id) pv, uniq(distinct_id) uv, product_id from evente.em_events_dis where org_id = ${orgId} and event = 'ViewProduct' and product_id in
${item}
and product_type = '${productType}' group by product_id ) a left join ( select sum(product_sub_price + freight_money) orderAmount, uniq(distinct_id) orderUser, uniq(increment_id) orderCnt, sum(product_number) productCnt, product_id from evente.em_events_dis where event = 'PaidOrder' and org_id = ${orgId} and product_id in
${item}
and product_type = '${productType}' group by product_id ) b on a.product_id = b.product_id

select a.device_type agentId, a.pv pv, a.uv uv, b.orderAmount orderAmount, b.orderUser orderUser, b.orderCnt orderCnt, b.productCnt productCnt, b.orderUser/a.uv conversionRate from ( select count(distinct_id) pv, uniq(distinct_id) uv, device_type from evente.em_events_dis where org_id = ${orgId} and event = 'ViewProduct' and device_type in
${item}
and product_type = '${productType}' group by device_type ) a left join ( select sum(product_sub_price + freight_money) orderAmount, uniq(distinct_id) orderUser, uniq(increment_id) orderCnt, sum(product_number) productCnt, device_type from evente.em_events_dis where event = 'PaidOrder' and org_id = ${orgId} and device_type in
${item}
and product_type = '${productType}' group by device_type ) b on a.device_type = b.device_type

select a.product_id productId, a.pv pv, a.uv uv, b.orderAmount orderAmount, b.orderUser userCnt, b.orderCnt orderCnt, b.productCnt productCnt from ( select count(distinct_id) pv, uniq(distinct_id) uv, product_id from evente.em_events_dis where event = 'ViewProduct' and product_id in
${item}
and product_type = '${productType}' group by product_id ) a left join ( select sum(product_sub_price + freight_money) orderAmount, uniq(distinct_id) orderUser, uniq(increment_id) orderCnt, sum(product_number) productCnt, product_id from evente.em_events_dis where event = 'PaidOrder' and product_id in
${item}
and product_type = '${productType}' group by product_id ) b on a.product_id = b.product_id
